mapper.select:
		select count(f_id) from items where 1=1
		
          and f_pid  like CONCAT('%',#{fPid,jdbcType=VARCHAR},'%')
        
		
          and f_key  like CONCAT('%',#{fKey,jdbcType=VARCHAR},'%')
        
        
          and f_title  like CONCAT('%',#{fTitle,jdbcType=VARCHAR},'%')
        
        
          and f_type  = #{fType,jdbcType=INTEGER}
        
        
          and f_opennewtab =  #{fOpennewTab,jdbcType=VARCHAR}
        
         
          and f_status  = #{fStatus,jdbcType=INTEGER}
        
        
  			
  			and f_createtime >= #{startTime,jdbcType=TIMESTAMP}
  			
  		
  		
  			
  			and f_createtime <= #{endTime,jdbcType=TIMESTAMP}

		select * from items where 1=1
		
          and f_pid  like CONCAT('%',#{fPid,jdbcType=VARCHAR},'%')
        
		
          and f_key  like CONCAT('%',#{fKey,jdbcType=VARCHAR},'%')
        
        
          and f_title  like CONCAT('%',#{fTitle,jdbcType=VARCHAR},'%')
        
        
          and f_type  = #{fType,jdbcType=INTEGER}
        
        
          and f_opennewtab =  #{fOpennewTab,jdbcType=VARCHAR}
        
         
          and f_status  = #{fStatus,jdbcType=INTEGER}
        
        
  			
  			and f_createtime >= #{startTime,jdbcType=TIMESTAMP}
  			
  		
  		
  			
  			and f_createtime <= #{endTime,jdbcType=TIMESTAMP}
  			
  		
		order by f_createtime desc limit limit #{pageNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

		select * from items where 1=1
		
          and f_id  = #{fId,jdbcType=VARCHAR}
        
		
          and f_key  = #{fKey,jdbcType=VARCHAR}
        
         
          and f_status  = #{fStatus,jdbcType=INTEGER}

		select * from items where 1=1
		
          and f_pid  = #{fPid,jdbcType=VARCHAR}
        
         
          and f_status  = #{fStatus,jdbcType=INTEGER}
