  `sources` varchar(255) DEFAULT NULL,
  `createtime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  `content` longtext,
  `type` int(11) DEFAULT NULL COMMENT '1为行业新闻，2公司新闻',
